import { useState } from 'react';

const SideBar = () => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`h-screen bg-slate-600 ${isOpen ? 'w-64' : 'w-0'} overflow-hidden transition-all duration-300 ease-in-out`}>
      {/* Sidebar Content */}
      <div className="p-4">
        <h2 className="text-white text-lg font-bold">Sidebar</h2>
        <ul className="mt-4">
          <li className="text-white">Menu Item 1</li>
          <li className="text-white">Menu Item 2</li>
          <li className="text-white">Menu Item 3</li>
          <li className="text-white">Menu Item 4</li>
        </ul>
      </div>
      {/* Toggle Button */}
      <button
        className="fixed top-0 left-0 m-4 text-white block lg:hidden"
        onClick={toggleSidebar}
      >
        {isOpen ? 'Hide Sidebar' : 'Show Sidebar'}
      </button>
    </div>
  );
};

export default SideBar;
