import React, {useState} from 'react';
import {Link,Redirect} from 'react-router-dom';
import {connect} from 'react-redux';


const Login = () => {
    const [formData,setFormData] = useState({
        email:'',
        password:''
    });

    const {email,password} = formData;

    const onChange = e => setFormData({...formData,[e.target.name]:e.target.value})

    const onSubmit = e => {
        e.preventDefault();
        //login(email,password)
    };

    //isAuthenticated?
    //Redirect them to the home page
    return (
        <div>
            <h1>Login</h1>
            <form onSubmit = {e=>onSubmit(e)}>
                <div>
                    <input className="form-control" type='email' placeholder = 'Email' name='email' value = {email} onChange = {e=>onChange(e)} required />
                </div>
                <div>   
                    <input className='form-control' type='password' placeholder = 'Password' name='password' value = {password} onChange = {e=>onChange(e)} minLength='8' required />
                </div>
               <button className='btn btn-primary'>Login</button>
            </form>
        </div>
    );
}

// const mapStateToProps = state =>(
//     {
//         //isAuthenticated?
//     }
// );

export default connect(null,{})(Login);
