import React, { useEffect, useState } from 'react';
import ProgressBar from "@ramonak/react-progress-bar";
import axios from 'axios';

export default function AttendanceCard() {
    const [attendance, setAttendance] = useState(0); // Initialize with 0
    const std_id = '221851';

    useEffect(() => { // Correct useEffect syntax
        const fetchData = async () => {
            let config = {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `JWT ${localStorage.getItem('access')}`,
                    'Accept': 'application/json'
                }
            };

            try {
                const response = await axios.get(`http://127.0.0.1:8000/api/attendance/${std_id}/`, config);
                setAttendance(response.data.attendance_percentage); // Assuming the API returns 'attendance_percentage'
            } catch (error) {
                console.log("Couldn't get attendance");
            }
        };

        fetchData();
    }, []); 

    return (
        <div class="w-[35rem] p-3 bg-black rounded-[1rem]">
            <h1 class="text-white">Attendance</h1>
            <ProgressBar class="w-[1rem]" completed={attendance} bgColor='#4942bc' animateOnRender />
        </div>
    );
}
