import { useState } from 'react';

export default function Dashboard() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        {/* Toggle Button */}
        <div className="col-md-12 d-md-none">
          <button
            className="btn btn-primary"
            onClick={toggleSidebar}
          >
            {isSidebarOpen ? 'Hide Sidebar' : 'Show Sidebar'}
          </button>
        </div>
        {/* Fixed Sidebar */}
        <div className={`col-md-4 col-lg-3 bg-dark position-fixed vh-100 ${isSidebarOpen ? 'show' : 'collapse'}`}>
          <ul className="list-group">
            <li className="list-group-item">Menu Item 1</li>
            <li className="list-group-item">Menu Item 2</li>
            <li className="list-group-item">Menu Item 3</li>
            <li className="list-group-item">Menu Item 4</li>
          </ul>
        </div>
        {/* Main Content */}
        <div className={`col-md-8 col-lg-9 ${isSidebarOpen ? 'offset-md-4 offset-lg-3' : 'offset-0'}`}>
          {/* Main content goes here */}
        </div>
      </div>
    </div>
  );
}
