import react from 'react';
export default function SideBar(){
  let menu_items = ['Student Info','Registeration','Courses'];
    return(
        <div class = 'flex-col  w-[15rem] bg-gradient-to-t from-[#384dbb] to-[#515d9a] h-[95vh] sticky rounded-[1.2rem] m-[1.2rem] col-span-1'>
          <div class='flex-col'>  
              <img src = "%PUBLIC_URL%/logo.png" class='w-[8rem] rounded-[1.2rem]' />
              <p class='text-white drop-shadow-md text-center text-3xl tracking-wide'>Dashboard</p>
          </div>
          <div class='flex-col'>
                  <ul class=''>
                    {menu_items.map((menuItem, index) => (
                        <li key={index} class='text-white font-bold text-center mt-2 mb-2 pt-2 pb-2 hover:bg-[#5367c9] transition duration-300 ease-in-out'> {menuItem} </li>
                  ))}
        </ul>
          </div>
        </div>
    );
}
