import { useState } from 'react';

const SideBar = () => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };
  const userName = "Shahab Khanzada";
  const toggleButtonClass = isOpen
    ? 'h-8 w-8 text-red-500 rotate-90 transition-transform duration-300 ml-[4rem]'
    : 'h-8 w-8 text-red-500 transition-transform duration-300 ml-[4rem]';

  return (
    <div className={`h-screen ${isOpen ? 'w-16' : 'w-0'} overflow-hidden transition-all duration-300 ease-in-out bg-gradient-to-t from-[#394ebc] to-[#5539bc] grid justify-center text-white`}>
      {/* Sidebar Content */}
      <button
        className="fixed top-0 left-0 m-4 text-white block"
        onClick={toggleSidebar}
      >
        <svg className={toggleButtonClass} fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 10h16M4 14h16M4 18h16"/>
        </svg>
      </button>
      <div className="p-4">
        <div class="mt-10">
            <img class='rounded-[50%]' src='https://placehold.co/80'/>
        </div>
        <div>
        <ul className="mt-4">
          <li className="">Menu Item 1</li>
          <li className="">Menu Item 2</li>
          <li className="">Menu Item 3</li>
          <li className="">Menu Item 4</li>
        </ul>
        </div>
      </div>
    </div>
  );
};

export default SideBar;
