import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import {connect} from 'react-redux';
import { login } from '../../Actions/auth';

const Login = ({login,isAuthenticated}) => {
    const [formData,setFormData] = useState({
        email:'',
        password:''
    });

    const {email,password} = formData;

    const onChange = e => setFormData({...formData,[e.target.name]:e.target.value})

    const onSubmit = e => {
        e.preventDefault();
        login(email,password);
    };
    const navigate = useNavigate();
    //isAuthenticated?
    if (isAuthenticated)
        navigate('/dashboard')
        
    //Redirect them to the home page
    return (
        <div class="grid grid-flow-col grid-cols-5 w-[100vw] h-[100vh] font-poppins">
            <div class="bg-[#070912] grid justify-center place-items-center lg:col-span-2 : md col-span-5">
                <div class="w-[27rem] h-[20rem]">
                    <h2 class="text-white text-[5rem]">Login</h2>
                    <form onSubmit={e=>onSubmit(e)}>
                        <div class="w-[18rem]">
                            <input class="w-[27rem] bg-transparent h-[2.5rem] mt-4 border-solid border-b-2 border-[#3b3d44] active:bg-transparent focus:outline-none focus:border-white focus:text-white transition duration-300 ease-in-out" type='email' placeholder='Email' name='email' value={email} onChange={e=>onChange(e)} required/>
                        </div>
                        <div class="w-[20rem]">
                            <input class="w-[27rem] bg-transparent h-[2.5rem] mt-4 border-solid border-b-2 border-[#3b3d44] active:bg-transparent focus:outline-none focus:border-white focus:text-white transition duration-300 ease-in-out" type='password' placeholder='Password' name='password' value={password} onChange={e=>onChange(e)} required/>
                        </div>
                        <div>
                            <button class='w-[27rem] h-[3rem] mt-6 rounded-[0.5rem] bg-[#394ebc] text-white hover:bg-[#4f5eac] transition duration-250 ease-in-out' type='submit'>Login</button>
                        </div>
                    </form>
                </div>
                
            </div> 
            <div class="hidden sm:bg-[#394ebc] sm:col-span-3 sm:block lg:grid lg:items-center lg:justify-center">
                    <div class="w-[32rem]">
                        <div>
                            <h1 class="text-white text-[4rem] font-bold tracking-widest">Welcome to</h1>
                            <h1 class = "text-white text-[4rem]">student portal</h1>
                        </div>
                     <img src="https://iili.io/JiIz9P2.png" class="w-full h-auto" alt="Login" border="0"/> 
                     </div>
            </div>
        </div>
    );
    

}
 const mapStateToProps = state =>(
     {
         isAuthenticated : state.auth.isAuthenticated
     }
 );

export default connect(mapStateToProps,{login})(Login);
