import { useState } from 'react';

const SideBar = () => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const sidebarsvg = `${<svg class="h-8 w-8 text-red-500"  fill="none" viewBox="0 0 24 24" stroke="currentColor">
  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 6h16M4 10h16M4 14h16M4 18h16"/>
</svg>}`

  return (
    <div className={`h-screen bg-slate-600 ${isOpen ? 'w-64' : 'w-0'} overflow-hidden transition-all duration-300 ease-in-out`}>
      {/* Sidebar Content */}
      <div className="p-4">
        <h2 className="text-white text-lg font-bold">Sidebar</h2>
        <ul className="mt-4">
          <li className="text-white">Menu Item 1</li>
          <li className="text-white">Menu Item 2</li>
          <li className="text-white">Menu Item 3</li>
          <li className="text-white">Menu Item 4</li>
        </ul>
      </div>
      {/* Toggle Button */}
      <button
        className="fixed top-0 left-0 m-4 text-white block"
        onClick={toggleSidebar}
      >
        {isOpen ? 'Hide Sidebar' : 'Show Sidebar'}
      </button>
    </div>
  );
};

export default SideBar;
