import {useState,useEffect} from 'react'


export default function ProfileCard(){
    const [Name,setName] = useState()
    useEffect(() => {
        function getName() {
          setName(localStorage.getItem("user_first_name") + " " + localStorage.getItem("user_last_name"));
        }
        getName();
      }, []);
    return(
        <div class = "grid grid-flow-col grid-cols-3 w-[75rem] h-[5rem] rounded-[3rem] mt-5 justify-center items-center bg-gradient-to-r justify-self-end from-[#384dbb] to-[#515d9a] ">
            <img class="rounded-[50%] col-span-1 m-2" src="https://placehold.co/60"/>
            <h1 class="col-span-1 text-white text-[5rem] text-center">{Name}</h1>
            <div class="grid grid-flow-col col-span-2 justify-end items-center">
            <img class="m-2 w-[2rem]" src = "https://iili.io/JrJTAZX.png"/>
            <img class="m-2 w-[2.25rem]" src = "https://iili.io/JrJIRrx.png"/>
            </div>
        </div>
    );
}
