import react from 'react'
import {useState,useEffect} from 'react'
export default function WelcomeCard(){
    const [FirstName,setFirstName] = useState()
    useEffect(() => {
        function getName() {
          setFirstName(localStorage.getItem("user_first_name"));
        }
        getName();
      }, []);
    
    return(
        <div className="w-full p-4 bg-gradient-to-r from-blue-700 to-blue-500 rounded-lg text-white">
        <p className="text-lg">{new Date().toLocaleDateString()}</p>
        <h1 className="text-4xl font-bold">Welcome Back, [FirstName]!</h1>
        <p className="text-lg">Always stay updated in your Student Portal!</p>
    </div>
    );
}

function currentDate(){
    let day = new Date().toLocaleString("en-US", { day : '2-digit'})
    let month = new Date().toLocaleString("en-US", { month: "long" })
    let year = new Date().getFullYear()

    let date = `${day}-${month}-${year}`;

    return date
}
