import { useState, useEffect } from 'react';

export default function ProfileCard() {
    const [name, setName] = useState('');
    const [time, setTime] = useState('');

    useEffect(() => {
        function getName() {
            const firstName = localStorage.getItem("user_first_name") || "First";
            const lastName = localStorage.getItem("user_last_name") || "Last";
            setName(`${firstName} ${lastName}`);
        }
        getName();
    }, []);

    useEffect(() => {
        function updateClock() {
            const now = new Date();
            const hours = now.getHours().toString().padStart(2, '0');
            const minutes = now.getMinutes().toString().padStart(2, '0');
            const seconds = now.getSeconds().toString().padStart(2, '0');
            setTime(`${hours}:${minutes}:${seconds}`);
        }

        updateClock();

        const intervalId = setInterval(updateClock, 1000);

        return () => clearInterval(intervalId);
    }, []);

    return (
        <div className="w-full p-4 bg-gradient-to-r from-blue-700 to-blue-500 rounded-lg flex flex-col items-center text-white">
        <img className="rounded-full w-16 h-16" src="https://placehold.co/60" alt="Profile" />
        <h1 className="text-2xl mt-4">Profile Name</h1>
        </div>
    );
}
